import React from 'react';
import axios from 'axios';
import '../css/Match.css'

export default class Match extends React.Component () {
    constructor(props) {
        super(props);
        this.deleteMatch = this.deleteMatch.bind(this);
    }

    deleteMatch() {
        axios.delete("http://localhost:5000/match/delete/" + this.props._id)
            .then(res => console.log(res))
            .catch(err => console.log(err));
    }

    render() {
        return (
            <div className="col-6 m-3 text-center p-3 match-card rounded">
                <div className="row">
                    <div className="col-4">
                        <strong>{this.props.match.team1}</strong>
                        <hr />
                        <div className="text-muted">
                            {this.props.match.country1}
                        </div>
                    </div>
                    <div className="col-1">
                        {this.props.match.score1}
                    </div>
                    <div className="col-2">
                        -
                    </div>
                    <div className="col-1">
                        {this.props.match.score2}
                    </div>
                    <div className="col-4">
                        <strong>{this.props.match.team2}</strong>
                        <hr />
                        <div className="text-muted">
                            {this.props.match.country2}
                        </div>
                    </div>
                </div>
                <br />
                <button className="btn btn-danger" onClick={this.deleteMatch}>Delete</button>
            </div>
        )
    }
}
