import React from 'react';
import axios from 'axios';

export default class AddTeam extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            name: "",
            country: ""
        }

        this.handleNameChange = this.handleNameChange(this);
        this.handleCountryChange = this.handleCountryChange(this);
        this.handleSubmit = this.handleSubmit(this);
    }

    handleNameChange = e => {
        this.setState({
            name: e.target.value
        })
    }

    handleSubmit = e => {
        e.preventDefault();

        axios.post()
    }

    render() {
        return (
            <div className="container my-3">
                <h3 className="my-2">Add a new team!</h3>

                <form onSubmit={this.handleSubmit()}>

                    <div className="form-group">
                        <label>Team name</label>
                        <input type="text" className="form-control" onChange={this.handleNameChange()} />
                    </div>

                    <div className="form-group">
                        <label>Country</label>
                        <input type="text" className="form-control" onChange={this.handleCountryChange()} />
                    </div>

                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>

            </div>
        )
    }
}
